    public void CarregarFoto() {

        jImg_foto.setText(null);
        FileNameExtensionFilter filtro = new FileNameExtensionFilter("Arquivo de Imagem (*.jpg, *.gif, *.png, *.jpeg, *.bmp)", "jpg", "gif", "png", "jpeg", "bmp");
        JFileChooser chooser = new JFileChooser();
        chooser.setFileFilter(filtro);
        int resultado = chooser.showOpenDialog(null);
        if (resultado == JFileChooser.APPROVE_OPTION) {

            //INSTANCIA DO SELETOR DE ARQUIVOS
            File f = chooser.getSelectedFile();
            //CAMINHO DO ARQUIVO SELECIONADO
            filename = f.getAbsolutePath();
            //SALVA A IMAGEM SELECIONADA NA VARIAVEL
            ImageIcon image = new ImageIcon(chooser.getSelectedFile().getPath());
            //ATRIBUI A IMAGEM AO LABEL
            jImg_foto.setIcon(new ImageIcon(image.getImage().getScaledInstance(130, 150, Image.SCALE_DEFAULT)));
            jText_nomeFoto.setText(filename);

        }
        try {

            File img = new File(filename);
            FileInputStream FIS = new FileInputStream(img);
            ByteArrayOutputStream BAOS = new ByteArrayOutputStream();
            byte[] buf = new byte[1024];
            for (int leiaNum; (leiaNum = FIS.read(buf)) != -1;) {

                BAOS.write(buf, 0, leiaNum);

            }

            person_image = BAOS.toByteArray();

        } catch (Exception ex) {

            JOptionPane.showMessageDialog(null, "Nenhuma imagem selecionada! \n" + ex);

        }

    }

    public void salvaImagem() {

        try {

            String caminho1 = jText_nomeFoto.getText();
            Files.copy(Paths.get(caminho1), Paths.get(recebe_caminho_img + jText_cpf.getText().replace(".", "").replace("-", "") + ".jpg"));
            String novo_caminho = recebe_caminho_img + jText_cpf.getText().replace(".", "").replace("-", "") + ".jpg";
            NOVO_CAMINHO_DA_IMAGEM = novo_caminho;
            JOptionPane.showMessageDialog(null, "A foto do Funcionario esta salva com sucesso na Biblioteca do Sistema!");

        } catch (IOException ex) {
            Logger.getLogger(rplorcamentoTelaCadFuncionario.class
                    .getName()).log(Level.SEVERE, null, "Erro ao salvar foto na biblioteca do sistema. \n" + ex);
        }
    }

    public void EventoSituacao() {

        if (jText_situacao.isSelected()) {
            jText_situacao.setText("Ativo");
        } else {
            jText_situacao.setText("Inativo");
        }

    }

    public void EventoDesligamento() {

        if (jChbx_desligamento.isSelected()) {
            jText_desligamento.setEnabled(true);
            jText_motivoDesligamento.setEnabled(true);
        } else {
            jText_desligamento.setEnabled(false);
            jText_motivoDesligamento.setEnabled(false);
            jText_desligamento.setText(null);
            jText_motivoDesligamento.addItem("null");
        }

    }

