        public void SalvarMaodeobra() throws ClassNotFoundException {

        ArrayList<String> Registro = new ArrayList<>();
        Registro.add(CODIGO_MAO_DE_OBRA);
        Registro.add(jText_tiposProfissionais.getText());
        String turno = (String) jCbx_turnoTrabalho.getSelectedItem();
        Registro.add(turno);
        Registro.add(jText_diasDeTrabalho.getValue().toString());
        Registro.add(jText_salario.getText().replace(",", "."));
        Registro.add(jText_salarioFinal.getText().replace(",", "."));
        Registro.add(jText_referencia.getText());

        rplorcamentoMaodeobraControl ControllerCidade = new rplorcamentoMaodeobraControl();
        try {
            //
            ControllerCidade.Salvar(Registro);
            //

// TODO add your handling code here:
        } catch (ParseException ex) {

            JOptionPane.showMessageDialog(null, "Erro Fatal! Erro, " + ex + ". Informações inseridas incorretamentes!");

            Logger.getLogger(rplorcamentoTelaCadMaodeobra.class.getName()).log(Level.SEVERE, null, ex);
        }

        LimparCampos();

    }

        public void LimparCampos(){
            
            jText_tiposProfissionais.setText(null);
            jCbx_turnoTrabalho.setSelectedItem(null);
            jText_diasDeTrabalho.setModel(new javax.swing.SpinnerNumberModel(Integer.valueOf(0), null, null, Integer.valueOf(1)));
            jText_salario.setText(null);
            jText_salarioFinal.setText(null);
            jText_referencia.setText(null);
            
        }
    
