    public void CalcularSalario() {

        int dias_de_trabalho = Integer.parseInt(jText_diasDeTrabalho.getValue().toString());
        Double salario = Double.parseDouble(jText_salario.getText().replace(",", "."));
        Double salario_final = dias_de_trabalho * salario;
        String strg_vl_total = String.valueOf(salario_final);

        jText_salarioFinal.setText(strg_vl_total.replace(".", ","));

    }

    
